import {Component} from 'react'
import Loader from 'react-loader-spinner'
import LanguageFilterItem from '../LanguageFilterItem'
import RepositoryItem from '../RepositoryItem'
import './index.css'

const languageFiltersData = [
  {id: 'ALL', language: 'All'},
  {id: 'JAVASCRIPT', language: 'Javascript'},
  {id: 'RUBY', language: 'Ruby'},
  {id: 'JAVA', language: 'Java'},
  {id: 'CSS', language: 'CSS'},
]

const views = {
  success: 'SUCCESS',
  failure: 'FAILURE',
  loading: 'LOADING',
}

// Write your code here
class GithubPopularRepos extends Component {
  state = {
    filterClick: languageFiltersData[0].id,
    view: '',
    calledData: [],
  }

  componentDidMount() {
    this.getData()
  }

  onClickLanguageChange = id => {
    this.setState({filterClick: id}, this.getData)
  }

  getData = async () => {
    this.setState({isLoading: true})
    const {filterClick} = this.state
    const url = ` https://apis.ccbp.in/popular-repos?language=${filterClick}`
    const options = {
      method: 'GET',
    }
    const response = await fetch(url, options)
    const data = await response.json()
    console.log(data)
    if (response.ok === true) {
      const filteredArray = data.popular_repos.map(eachItem => ({
        name: eachItem.name,
        id: eachItem.id,
        issuesCount: eachItem.issues_count,
        forksCount: eachItem.forks_count,
        starsCount: eachItem.stars_count,
        avatarUrl: eachItem.avatar_url,
      }))
      this.setState({calledData: filteredArray, isLoading: false})
    }
  }

  renderMainContent = () => {
    const {filterClick, calledData, isLoading} = this.state
    return (
      <>
        {' '}
        <h1>Popular</h1>
        <ul className="language-options-list">
          {languageFiltersData.map(eachItem => (
            <LanguageFilterItem
              key={eachItem.id}
              data={eachItem}
              onClickLanguageChange={this.onClickLanguageChange}
              isTrue={eachItem.id === filterClick}
            />
          ))}
        </ul>
        {isLoading ? (
          this.renderLoader()
        ) : (
          <>
            <ul className="repository-list">
              {calledData.map(eachItem => (
                <RepositoryItem data={eachItem} key={eachItem.id} />
              ))}
            </ul>
          </>
        )}
      </>
    )
  }

  renderLoader = () => (
    <div data-testid="loader">
      <Loader type="ThreeDots" color="#0284c7" height={80} width={80} />
    </div>
  )

  render() {
    const {isFailed} = this.state
    return <div className="main-container">{this.renderMainContent()}</div>
  }
}

export default GithubPopularRepos
